function currentTime() {
	var a = new Date;
	var b = new Array;b[0]="Januari";
		b[1]="Februari";
		b[2]="Maret";
		b[3]="April";
		b[4]="Mei";
		b[5]="Juni";
		b[6]="Juli";
		b[7]="Agustus";
		b[8]="September";
		b[9]="Oktober";
		b[10]="November";
		b[11]="Desember";
	currentMonth = b[a.getMonth()];
	currentYear  = a.getFullYear();
	currentDate  = a.getDate();

	var c = new Array;
		c[0]="Minggu";
		c[1]="Senin";
		c[2]="Selasa";
		c[3]="Rabu";
		c[4]="Kamis";
		c[5]="Jum'at";
		c[6]="Sabtu";
		currentDay = c[a.getDay()];

	var d = a.getHours();
	var e = a.getMinutes();
	var f = a.getSeconds();
		d = (d < 10 ? "0" : "") + d;
		e = (e < 10 ? "0" : "") + e;
		f = (f < 10 ? "0" : "") + f;
	var h = "<span class='day'>" + currentDay + " " + currentDate + " " + currentMonth + " " + currentYear + "</span> <span class='time'>" + d + " : " + e + " : " + f + "</span>";
	$("#clock").html(h)
}
